  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'
  import { Playground } from 'docz'
import { Scene } from '../src'

export default ({components, ...props}) => <MDXTag name="wrapper"  components={components}><MDXTag name="h1" components={components} props={{"id":"getting-started"}}>{`Getting Started`}</MDXTag>
<MDXTag name="p" components={components}>{`Sprite React is a JavaScript library for drawing complex canvas graphics using `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://reactjs.org/"}}>{`React`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="p" components={components}>{`It provides declarative and reactive bindings to the `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://spritejs.org"}}>{`SpriteJS`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"installation"}}>{`Installation`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-bash"}}>{`npm install sprite-react --save
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"examples"}}>{`Examples`}</MDXTag>


<Playground __position={0} __code={`<Scene>
  <layer>
    <group
      display="flex"
      justifyContent="space-around"
      width={300}
      anchor={[0.5, 0.5]}
      pos={[150, 150]}
      bgcolor="#eee"
    >
      <sprite
        size={[50, 50]}
        bgcolor="red"
        opacity={0.3}
        onMouseEnter={function() {
          this.attr(\'opacity\', 1.0)
        }}
        onMouseLeave={function() {
          this.attr(\'opacity\', 0.3)
        }}
      />
      <sprite
        size={[50, 50]}
        bgcolor="green"
        opacity={0.3}
        onMouseEnter={function() {
          this.attr(\'opacity\', 1.0)
        }}
        onMouseLeave={function() {
          this.attr(\'opacity\', 0.3)
        }}
      />
      <sprite
        size={[50, 50]}
        bgcolor="blue"
        opacity={0.3}
        onMouseEnter={function() {
          this.attr(\'opacity\', 1.0)
        }}
        onMouseLeave={function() {
          this.attr(\'opacity\', 0.3)
        }}
      />
    </group>
  </layer>
</Scene>`} __scope={{props,Scene}}>
  <Scene>
    <layer>
      <group display='flex' justifyContent="space-around" width={300} anchor={[0.5, 0.5]} pos={[150, 150]} bgcolor="#eee">
        <sprite size={[50, 50]} bgcolor='red' opacity={0.3} 
          onMouseEnter={function(){this.attr('opacity', 1.0)}}
          onMouseLeave={function(){this.attr('opacity', 0.3)}}>
        </sprite>
        <sprite size={[50, 50]} bgcolor='green' opacity={0.3}
          onMouseEnter={function(){this.attr('opacity', 1.0)}}
          onMouseLeave={function(){this.attr('opacity', 0.3)}}>
        </sprite>
        <sprite size={[50, 50]} bgcolor='blue' opacity={0.3}
          onMouseEnter={function(){this.attr('opacity', 1.0)}}
          onMouseLeave={function(){this.attr('opacity', 0.3)}}>
        </sprite>
      </group>
    </layer>
  </Scene>
